"use strict";

import { Model } from "sequelize";

module.exports = (sequelize, DataTypes) => {
  class CryptoNetwork extends Model {
    /**
     * Helper method for defining associations.
     * This method is not a part of Sequelize lifecycle.
     * The `models/index` file will call this method automatically.
     */
    static associate(models) {
      CryptoNetwork.belongsTo(models.Currencies, {
        as: "currency",
        foreignKey: "currency_id",
      });
    }
  }
  CryptoNetwork.init({
    currency_id: DataTypes.INTEGER,
    network_name: DataTypes.STRING,
    network_image: DataTypes.STRING
  }, {
    sequelize,
    modelName: "CryptoNetworks",
    tableName: "CryptoNetworks",
    freezeTableName: true,
  });

  return CryptoNetwork;
};

