sourcesContent:
"use strict";

import models from "../database/models";
import Response from "../utils/response";
import CryptoNetworkValidator from "../utils/validators/cryptoNetwork_validator";

const { CryptoNetworks } = models;

/**
 * @class CryptoNetworkController
 **/
class CryptoNetworkController {

    /**
     * @function getCryptoNetworks, (Get all crypto networks).
     **/
    static getCryptoNetworks = async (req, res) => {
        try {
            const cryptoNetworks = await CryptoNetworks.findAll();
            if (!cryptoNetworks.length) {
                const response = new Response(
                    false,
                    404,
                    "No crypto network found."
                );
                return res.status(response.code).json(response);
            }

            const response = new Response(
                true,
                200,
                'Crypto networks retrieved successfully.',
                { cryptoNetworks }
            );
            return res.status(response.code).json(response);

        } catch (error) {
            console.log(`ERROR::: ${error}`);

            const response = new Response(
                false,
                500,
                'Server error, please try again later.'
            );
            return res.status(response.code).json(response);
        }
    };


    /**
     * @function getCryptoNetwork, (Get a single crypto network).
     **/
    static getCryptoNetwork = async (req, res) => {
        try {
            const { id } = req.params;

            const cryptoNetwork = await CryptoNetworks.findByPk(id);
            if (!cryptoNetwork) {
                const response = new Response(
                    false,
                    404,
                    "No crypto network found."
                );
                return res.status(response.code).json(response);
            }

            const response = new Response(
                true,
                200,
                'Crypto network retrieved successfully.',
                { cryptoNetwork }
            );
            return res.status(response.code).json(response);

        } catch (error) {
            console.log(`ERROR::: ${error}`);

            const response = new Response(
                false,
                500,
                'Server error, please try again later.'
            );
            return res.status(response.code).json(response);
        }
    };


    /**
     * @function updateCryptoNetwork (Update a crypto network).
     **/
    static updateCryptoNetwork = async (req, res) => {
        try {
            const { id } = req.params;
            const requestBody = req.body;

            //  Validate the Request Body.
            const { error, value } = CryptoNetworkValidator.updateCryptoNetworkSchema.validate(requestBody);
            if (error) {
                const response = new Response(
                    false,
                    400,
                    `${error.message}`
                );
                return res.status(response.code).json(response);
            }

            const updatedCryptoNetwork = await CryptoNetworks.update({ ...value }, { where: { id } });
            if (updatedCryptoNetwork[0] === 0) {
                const response = new Response(
                    false,
                    400,
                    "Failed to update crypto network."
                );
                return res.status(response.code).json(response);
            }

            //  Get the updated crypto network back.
            const cryptoNetwork = await CryptoNetworks.findByPk(id);

            const response = new Response(
                true,
                200,
                "Crypto network updated successfully.",
                { cryptoNetwork }
            );
            return res.status(response.code).json(response);

        } catch (error) {
            console.log(`ERROR::: ${error}`);

            const response = new Response(
                false,
                500,
                'Server error, please try again later.'
            );
            return res.status(response.code).json(response);
        }
    };


    /**
     * @function deleteCryptoNetwork (Delete a crypto network).
     **/
    static deleteCryptoNetwork = async (req, res) => {
        try {
            const { id } = req.params;

            const deletedCryptoNetwork = await CryptoNetworks.destroy({ where: { id } });
            if (deletedCryptoNetwork === 0) {
                const response = new Response(
                    false,
                    400,
                    "Failed to delete crypto network."
                );
                return res.status(response.code).json(response);
            }

            const response = new Response(
                true,
                200,
                "Crypto network deleted successfully."
            );
            return res.status(response.code).json(response);

        } catch (error) {
            console.log(`ERROR::: ${error}`);

            const response = new Response(
                false,
                500,
                'Server error, please try again later.'
            );
            return res.status(response.code).json(response);
        }
    };
}

export default CryptoNetworkController;

