    "/all_data_plan",
    TokenValidation.userTokenValidation,
    DataPlanController.getDataPlans
);

//  Get single cable tv.
dataPlanRouter.get(
    "/single_data_plan/:code",
    TokenValidation.userTokenValidation,
    DataPlanController.getDataPlan
);


export default dataPlanRouter;

